INSERT INTO `solutions` (`context_id`, `assignment_id`, `text`, `best`) VALUES
(31, 1, 'V prilohe posielame fotku nasho timu, ako sme riesili 1. ulohu Letnej ligy FLL,<br />\r\na program, ktory sme spravili. Farebnym senzorom sme chceli zistovat, ci je<br />\r\nkocka zlta alebo nie, ale nefugovalo nam to. Este sme chceli, aby sa otacal<br />\r\na ked uvidi kocku, tak pojde k nej, ale nepodarilo sa nam to dokoncit.<br />\r\n<br /><br />\r\nVideo nasho riesenia:<br /> \r\n<iframe width="500" height="375" src="http://www.youtube.com/embed/rb3c3PCaw-o" frameborder="0" allowfullscreen></iframe>\r\n<br />\r\n<img src="http://robotika.sk/events/13FLL/LL/1kolo/Hexadron/HexadronTeamFoto.jpg" width=500/>\r\n<br />\r\n<br />\r\n<img src="http://robotika.sk/events/13FLL/LL/1kolo/Hexadron/LL-FLL-1kolo.png" />\r\n<br /><br />  \r\n<a href="http://robotika.sk/events/13FLL/LL/1kolo/Hexadron/LL-FLL-1kolo.rbt">LL-FLL-1kolo.rbt</a>', 1),
(32, 1, 'Robot má dva ultrazvukové senzory - horným vidí steny, dolným kocku.<br />\r\nKeď príde ku stene, oba senzory vidia stenu, otočí sa a hľadá ďalej. <br />\r\nKeď príde ku kocke, iba dolný senzor ju vidí, vtedy použije ruku, ktorou <br />\r\nkocku uchopí. Potom hľadá svetelným senzorom čiaru a keď ju nájde, <br />\r\nzastane.  <br /><br />\r\nV prilohe je obrazok a video - z prehladavania miestnosti a z uchopenia kocky.<br />\r\nProjekt sme zacali 1. tyzden, ale 2. tyzden sme odisli do skoly v prirode\r\n<br /><br />\r\nVideo:\r\n<a href="http://robotika.sk/events/13FLL/LL/1kolo/RoboIT/roboit_letnaliga1.avi">roboit_letnaliga1.avi</a>, <a href="http://robotika.sk/events/13FLL/LL/1kolo/RoboIT/roboit_letnaliga1_capture.avi">roboit_letnaliga1_capture.avi</a>\r\n<br />\r\n<br />\r\nObrázok robota:\r\n<br />\r\n<img src="http://robotika.sk/events/13FLL/LL/1kolo/RoboIT/roboit_letnaliga1.jpg" />  \r\n<br />\r\n<br />\r\nProgram:\r\n<br />\r\n\r\n<a href="http://robotika.sk/events/13FLL/LL/1kolo/RoboIT/roboitll1.png" target="_top">\r\n<img src="http://robotika.sk/events/13FLL/LL/1kolo/RoboIT/roboitll1.png" border="0" width="500"/></a>  \r\n<br />\r\n<br />\r\n<a href="http://robotika.sk/events/13FLL/LL/1kolo/RoboIT/roboitll1.rbt">roboitll1.rbt</a>', 0),
(33, 2, 'v prilohe posielame 2. ulohu z letnej ligy za team Legaci zo Ziaru nad Hronom\r\n\r\n<br /><br />\r\nVideo:<br /> \r\n<a href="http://robotika.sk/events/13FLL/LL/2kolo/Legaci/DSCF0654.avi">DSCF0654.avi</a>\r\n<br />\r\n<a href="http://robotika.sk/events/13FLL/LL/2kolo/Legaci/adamkarate.jpg" target="_top">\r\n<img src="http://robotika.sk/events/13FLL/LL/2kolo/Legaci/adamkarate.jpg" border="0" width="500"/></a>\r\n<br />\r\n<br />\r\n<a href="http://robotika.sk/events/13FLL/LL/2kolo/Legaci/program.png" target="_top">\r\n<img src="http://robotika.sk/events/13FLL/LL/2kolo/Legaci/program.png" border="0" width="500"/></a>\r\n<br /><br />  \r\n<a href="http://robotika.sk/events/13FLL/LL/2kolo/Legaci/KARATE.rbt">KARATE.rbt</a>', 0),
(34, 2, 'Zasielame Vám naše riešenie 2. úlohy.<br />\r\nTeam LeGorazda <br />\r\nZŠ s MŠ, ulica sv. Gorazda 1, Žilina <br />\r\n<br />\r\nMali sme menší problém s videom ale zvládli sme to ako chlapi.<br />\r\n<br />\r\n\r\n\r\n<br /><br />\r\nVideo:<br /> \r\n<iframe width="854" height="480" src="http://www.youtube.com/embed/NVKp17xVLw0" frameborder="0" allowfullscreen></iframe>\r\n<br />\r\n<a href="http://robotika.sk/events/13FLL/LL/2kolo/LeGorazda/DSCN9138.jpg" target="_top">\r\n<img src="http://robotika.sk/events/13FLL/LL/2kolo/LeGorazda/DSCN9138.jpg" width="500" border="0" /></a>\r\n<br />\r\n<br />\r\n<a href="http://robotika.sk/events/13FLL/LL/2kolo/LeGorazda/karate_legorazda.jpg" target="_top">\r\n<img src="http://robotika.sk/events/13FLL/LL/2kolo/LeGorazda/karate_legorazda.jpg" width="500" border="0" />\r\n<br /><br />  \r\n<a href="http://robotika.sk/events/13FLL/LL/2kolo/LeGorazda/karate.rbt">karate.rbt</a>', 1),
(35, 2, 'V prílohe posielame fotku nášho tímu a link s videom ako sa nám podarilo úlohu vyriešiť. <br />\r\nPoužili sme senzor farby v režime svetelného senzora. Odmerali sme čas, za aký prejde <br />\r\nrobot čiaru a nastavili sme kedy sa má spustiť motor s "rukou".\r\n<br />\r\n\r\n<br /><br />\r\nVideo:<br /> \r\n<iframe width="500" height="375" src="http://www.youtube.com/embed/GXQETHaCkEI" frameborder="0" allowfullscreen></iframe>\r\n<br />\r\n<a href="http://robotika.sk/events/13FLL/LL/2kolo/RJM_Team/karate.jpg" target="_blank">\r\n<img src="http://robotika.sk/events/13FLL/LL/2kolo/RJM_Team/karate.jpg" border="0" width="500" /></a>\r\n<br />\r\n<br />\r\n<a href="http://robotika.sk/events/13FLL/LL/2kolo/RJM_Team/program_karate.jpg" target="_top" />\r\n<img src="http://robotika.sk/events/13FLL/LL/2kolo/RJM_Team/program_karate.jpg" border="0" width="500" /></a>', 0),
(36, 2, 'Dobry den!\r\n<br /><br />\r\nPosielame riesenie 2.kola.\r\n<br /><br />\r\nVideo nasho riesenia:<br /> \r\n<iframe width="500" height="375" src="http://www.youtube.com/embed/eMC-7nsFmWk" frameborder="0" allowfullscreen></iframe>\r\n<br />\r\n<a href="http://robotika.sk/events/13FLL/LL/2kolo/Hexadron/HexadronTeamFoto.jpg" target="_top">\r\n<img src="http://robotika.sk/events/13FLL/LL/2kolo/Hexadron/HexadronTeamFoto.jpg" width="500" border="0" /></a>\r\n<br />\r\n<br />\r\n\r\n<a href="http://robotika.sk/events/13FLL/LL/2kolo/Hexadron/HexadronUloha2.png" target="_top" />\r\n<img src="http://robotika.sk/events/13FLL/LL/2kolo/Hexadron/HexadronUloha2.png" width="500" border="0"/></a>\r\n<br /><br />  \r\n<a href="http://robotika.sk/events/13FLL/LL/2kolo/Hexadron/HexadronUloha2.rbt">HexadronUloha2.rbt</a>', 0),
(37, 3, 'dobry den,<br />\r\n2 dni sme pracovali na tomto programe aj so stuhou.<br />\r\nnejde nam aj po dokladnom prevereni programu (nezachyti zelenu stuhu). (Branou 3.0)<br />\r\nv prilohe poslielam aj iny program, ktory nam zelenu zachyti. (skuska)\r\n<br /><br />\r\nVideo:<br /> \r\n<iframe width="854" height="480" src="http://www.youtube.com/embed/EoWbIbzvHa0" frameborder="0" allowfullscreen></iframe>\r\n<br />\r\n<img src="my.jpg" />\r\n<br />\r\n<br />\r\n<img src="branou.3.0.png" />\r\n<br />\r\n<img src="skuska.png" />\r\n<br /><br />  \r\n<a href="branou.3.0.rbt">branou.3.0.rbt</a>\r\n<br /><br />  \r\n<a href="skuska.rbt">skuska.rbt</a>', 0),
(38, 3, 'ZŠ s MŠ, Ulica sv. Gorazda 1, Žilina\r\n<br /><br />\r\nZasielame naše riešenie tretej úlohy. Problém nám trochu robilo zadné koliesko lebo sa otáčalo kam len chcelo ale keď sa to upravilo všetko bolo ok. Na fotke sme zas iný dvaja ako minule, vždy keď sa fotíme je niekto na exkurzii, výlete alebo u lekára.\r\n<br /><br />\r\nVideo:<br /> \r\n<iframe width="854" height="480" src="http://www.youtube.com/embed/JAAzWtX-OSs" frameborder="0" allowfullscreen></iframe>\r\n<br />\r\n<iframe width="854" height="480" src="http://www.youtube.com/embed/MOBnQcw0Qr8" frameborder="0" allowfullscreen></iframe>\r\n<br />\r\n<img src="DSC_0327.jpg" />\r\n<br />\r\n<br />\r\n<img src="branou.png" />\r\n<br /><br />  \r\n<a href="branou.rbt">branou.rbt</a>', 1),
(39, 3, 'V prílohe posielame fotografiu, link na video a program. Použili sme senzory farby na sledovanie pásu a tlakový senzor aby robot vedel kedy má kocku vziať a kedy pustiť.\r\n<br />\r\n\r\n<br /><br />\r\nVideo:<br /> \r\n<iframe width="854" height="480" src="http://www.youtube.com/embed/UHklqQW-R6w" frameborder="0" allowfullscreen></iframe>\r\n<br />\r\n<img src="team_foto.jpg" />\r\n<br />\r\n<br />\r\n<img src="branou.jpg" />\r\n<br /><br />  \r\n<a href="branou.rbt">branou.rbt</a>', 0),
(40, 3, 'Dobry den!\r\n<br /><br />\r\nPosielam riesenie tretieho kola za tim Hexadron.\r\n<br /><br />\r\nUlohu  sme riesili  v troch krokoch:\r\n1.robot sa dostane na svetlu ciaru a otoci sa,\r\n2.robot pride ku kocke a pritiahne ju na cierny stvorec,\r\n3.spojili sme to  - vyrobili sme vlastny blok OtocSa pre otacanie na mieste, neslo nam to bez toho.\r\n<br /><br />\r\nNemali sme uplne vhodne podmienky, lebo bol maly stol a nemali sme vhodnu podlozku.\r\n<br /><br />\r\nVideo nasho riesenia:<br /> \r\n<iframe width="854" height="480" src="http://www.youtube.com/embed/xRfNK_y9yXQ" frameborder="0" allowfullscreen></iframe>\r\n<br />\r\n<img src="FLL_32.jpg" />\r\n<br />\r\n<br />\r\n\r\n<img src="FLL_30.jpg" />\r\n<br /><br />  \r\n\r\n<img src="FLL_33.JPG" />\r\n<br /><br />  \r\n\r\n<img src="FLL_35.png" />\r\n<br /><br />  \r\n\r\n<img src="FLL_36.png" />\r\n<br /><br />  \r\n<a href="FLL_03.rbt">FLL_03.rbt</a>', 0),
(41, 3, 'Dobrý deň.<br />\r\nPosielame riešenie tretieho zadania letnej ligy (Bránou).<br />\r\nNáš tím sa volá RoboIT.<br />\r\n<br /><br />\r\nProgram a foto je v prilohe, video je tu: \r\n<br /><br />\r\nVideo:\r\n<a href="VIDEO0021.mp4">VIDEO0021.mp4</a>\r\n<br /><br />\r\n<img src="IMAG0060.jpg" />  \r\n<br />\r\n<br />\r\nProgram:\r\n<br />\r\n<img src="program.png" />  \r\n<br />\r\n<br />\r\n<a href="znacka.rbt">znacka.rbt</a>', 0),
